import { useState } from 'react';
import {RiCloseCircleLine, RiCheckboxBlankCircleLine} from 'react-icons/ri'
import {TiEdit} from 'react-icons/ti'
import { styled } from 'styled-components';


const ListItemStyles = styled.div`
display: flex;
align-items: center;
	justify-content: center;
`

export default function Item({shoppingItems, completeShoppingItem, removeShoppingItem }) {
	const [edit, setEdit] = useState({
		id: null,
		value: '',
	});


	return shoppingItems.map((shoppingItem, index) => (
		<ListItemStyles>
<div>
	<RiCheckboxBlankCircleLine></RiCheckboxBlankCircleLine>
</div>
      <div key={shoppingItem.id} >
        {shoppingItem.text}
				<div>
					<RiCloseCircleLine onClick={() => removeShoppingItem(shoppingItem.id)}></RiCloseCircleLine>
					<TiEdit></TiEdit>
				</div>
      </div>
		</ListItemStyles>

	));
}
