import { useState } from 'react';
import { RiCloseCircleLine, RiCheckboxBlankCircleLine } from 'react-icons/ri';
import { TiEdit } from 'react-icons/ti';
import '../styles/shoppingItem.css';
import AddItemInput from './AddItemInput';
export default function Item({
	shoppingItems,
	completeShoppingItem,
	removeShoppingItem,
	editShoppingItem,
}) {
	const [edit, setEdit] = useState({
		id: null,
		value: '',
	});

	const submitUpdate = value => {
		editShoppingItem(edit.id, value)
		setEdit({
			id: null,
			value: '',
		})
	}

	if(edit.id) {
		return <AddItemInput edit={edit} onSubmit={submitUpdate} />
	}

	return shoppingItems.map((shoppingItem, index) => (
		<div className='shopping-item'>
			<div className='check-listItem'>
				<div className='checkbox'>
					<RiCheckboxBlankCircleLine size={21} />
				</div>
				<div key={shoppingItem.id}>{shoppingItem.text}</div>
			</div>
			<div className='shopping-list-edit-remove'>
				<RiCloseCircleLine
					size={21}
					onClick={() => removeShoppingItem(shoppingItem.id)}
				/>
				<TiEdit size={21} />
				onClick={() => editShoppingItem(shoppingItem.id)}
			</div>
		</div>
	));
}
