import { useState } from 'react';
import { RiCloseCircleLine, RiCheckboxBlankCircleLine } from 'react-icons/ri';
import { TiEdit } from 'react-icons/ti';
import '../styles/shoppingItem.css'
export default function Item({
	shoppingItems,
	completeShoppingItem,
	removeShoppingItem,
}) {
	const [edit, setEdit] = useState({
		id: null,
		value: '',
	});

	return shoppingItems.map((shoppingItem, index) => (
		<div className='shopping-item icon'>
			<div className='tick-box'>
			</div>
				<RiCheckboxBlankCircleLine
				size={21} 
			  />
			<div key={shoppingItem.id}>{shoppingItem.text}</div>
			<div className='shopping-list-edit-remove'>
				<RiCloseCircleLine
				size={21}
					onClick={() => removeShoppingItem(shoppingItem.id)}

				/>
				<TiEdit
				size={21} 
				/>
			</div>
		</div>
	));
}
