import { useState } from 'react';
import AddItemInput from './AddItemInput';
import Item from './Item';
import '../styles/shoppingList.css'

export default function List() {
	const [shoppingItems, setShoppingItems] = useState([]);

	const addShoppingItem = (shoppingItem) => {
		const newShoppingItems = [shoppingItem, ...shoppingItems];
		setShoppingItems(newShoppingItems);
	};

	const completeShoppingItem = id => {
		let updatedShoppingItems = shoppingItems.map(shoppingItem => {
			if(shoppingItem.id === id) {
				shoppingItem.isComplete = !shoppingItem.isComplete
			}
			return shoppingItem;
		});
		setShoppingItems(updatedShoppingItems)
	}

	const removeShoppingItem = id => {
		const removeArr = [...shoppingItems].filter(shoppingItem => shoppingItem.id !== id);
		setShoppingItems(removeArr)
	}

	return (
		<div className='shopping-list'>
			<AddItemInput onSubmit={addShoppingItem}/>
			<Item shoppingItems={shoppingItems} compleShoppingItem={completeShoppingItem} removeShoppingItem={removeShoppingItem}/>
		</div>
	);
}
